# -*- coding: UTF-8 -*-
import sys, os, random, pprint
sys.path.append(os.path.abspath('./src'))
from dungeon_generator import Dungeon

def main():
    config = {
        'row_size': 32,
        'col_size': 32,
        'room_number' : random.randint(3,10),
        'amount_water': 7,
    }
    dungeon = Dungeon(config=config)
    pprint.pprint( dungeon.to_array() )

main()
